import React, { useState } from 'react';
import logo from '../logoTC.png';

function MessagePage() {
  const [message, setMessage] = useState('');
  const [dateTime, setDateTime] = useState('');

  const handleChooseDateTime = (e) => {
    setDateTime(e.target.value);
  };

  const handleSubmit = () => {
    // handle form submission logic here
    console.log(`Submitting message: ${message}`);
    console.log(`Chosen date and time: ${dateTime}`);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ width: '400px', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
          <img src={logo} alt="Logo" />
        </div>
        <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Leave your message!</h1>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <label style={{ marginBottom: '10px', fontWeight: 'bold' }}>Message:</label>
          <textarea rows="5" value={message} onChange={(e) => setMessage(e.target.value)} style={{ marginBottom: '10px', width: '150%', height: '250px' }} />
          <label style={{ marginBottom: '10px', fontWeight: 'bold' }}>Choose Date and Time:</label>
          <input type="datetime-local" value={dateTime} onChange={handleChooseDateTime} style={{ marginBottom: '10px' }} />
          <br />
          <button onClick={handleSubmit} style={{ fontWeight: 'bold', marginTop: '10px' }}>Submit</button>
        </div>
      </div>
    </div>
  );
}

export default MessagePage;
