// export default Homepage;
import React, { useState, useEffect } from 'react';
import logo from '../../src/logo.png';
import axios from 'axios';

function HomePage() {
  const [numMessages, setNumMessages] = useState(0);

  useEffect(() => {
    // fetch the number of messages from the backend and update the state
    axios.get('http://localhost:3001/messages/count')
      .then((response) => {
        setNumMessages(response.data.count);
      })
      .catch((error) => {
        console.error('Failed to fetch number of messages:', error);
      });
  }, []);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ width: '400px', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
          <img src={logo} alt="Logo" />
        </div>
        <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Welcome to my App</h1>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <h2 style={{ marginBottom: '10px' }}>Inbox</h2>
          <p style={{ marginBottom: '10px' }}>You have {numMessages} messages</p>
          <button style={{ fontWeight: 'bold', marginTop: '10px' }}>Go to Inbox</button>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
