import React, { useState } from 'react';
import logo from '../../src/logoTC.png';

function MessagePage() {
  const [message, setMessage] = useState('');
  const [dateTime, setDateTime] = useState('');

  const handleChooseTime = () => {
    const date = new Date();
    const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

    const dateString = `${daysOfWeek[date.getDay()]}, ${months[date.getMonth()]} ${date.getDate()}, ${date.getFullYear()}, ${formatTime(date)}`;
    setDateTime(dateString);
  };

  const formatTime = (date) => {
    let hour = date.getHours();
    const mins = date.getMinutes();
    const ampm = hour >= 12 ? 'PM' : 'AM';

    hour = hour % 12;
    hour = hour ? hour : 12;
    const formattedMins = mins < 10 ? `0${mins}` : mins;

    return `${hour}:${formattedMins} ${ampm}`;
  };

  const handleSubmit = () => {
    // handle submit logic here
    console.log(`Submitting message: ${message} with date and time: ${dateTime}`);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
        <img src={logo} alt="Logo" />
      </div>
      <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Leave your message!</h1>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <label style={{ marginBottom: '10px', fontWeight: 'bold' }}>Message:</label>
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} style={{ marginBottom: '10px', width: '75%', height: '200px', resize: 'none' }} />
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <button onClick={handleChooseTime} style={{ marginRight: '10px' }}>Choose Time</button>
          <p>{dateTime}</p>
        </div>
        <br />
        <button onClick={handleSubmit} style={{ fontWeight: 'bold', marginTop: '10px' }}>Submit</button>
      </div>
    </div>
  );
}

export default MessagePage;
