import React from 'react';
import logo from '../../src/logoTC.png';

function MessagePage() {
  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '60%' }}>
        <img src={logo} alt="Logo" style={{ marginBottom: '20px' }} />
        <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontWeight: 'bold', fontSize: '3rem' }}>Leave Your Message!</h1>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '60%', marginBottom: '20px' }}>
          <label style={{ marginBottom: '10px' }}>
            Message:
          </label>
          <textarea style={{ height: '200px', marginBottom: '10px', fontSize: '1.5rem', padding: '10px' }} />
          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            <button style={{ fontWeight: 'bold', marginRight: '20px', fontSize: '1.2rem' }}>Choose Date & Time</button>
            <button style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>Submit</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MessagePage;
