import React, { useState, useEffect } from 'react';
import logo from '../../src/logo.png';
import axios from 'axios';

function HomePage() {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // fetch the list of messages from the backend and update the state
    axios.get('http://localhost:3001/messages')
      .then((response) => {
        setMessages(response.data.slice(0, 3));
      })
      .catch((error) => {
        console.error('Failed to fetch messages:', error);
      });
  }, []);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ width: '400px', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
          <img src={logo} alt="Logo" />
        </div>
        <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Welcome to my App</h1>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <h2 style={{ marginBottom: '10px' }}>Inbox</h2>
          {messages.map((message) => (
            <div key={message.id} style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', width: '100%' }}>
              <p style={{ fontWeight: 'bold', marginRight: '10px' }}>{message.title}</p>
              <p>{new Date(message.date).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
