import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../src/logoTC.png';
import axios from 'axios';
import { Link } from 'react-router-dom';

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const navigate = useNavigate();

  const handleRegister = () => {
    // handle register logic here
    // console.log(`Registering with username: ${username} and password: ${password}`);
    // axios.post(`http://localhost:3001/users/${username}/${password}`)
    //   .then(response => {
    //     console.log('Registration successful:', response.data);
    //     // redirect to the login page or show a success message
    //     navigate('/login');
    //   })
    //   .catch(error => {
    //     console.error('Registration failed:', error);
    //     // show an error message to the user
    //   });
    const data = JSON.stringify({
      "username": username,
      "password": password
    });

    const config = {
      method: 'post',
      maxBodyLength: Infinity,
      url: 'http://localhost:3001/users/',
      headers: {
      'Content-Type': 'application/json'
      },
      data : data
    };

    axios.request(config)
      .then((response) => {
      console.log(JSON.stringify(response.data));
      navigate('/login');
      })
      .catch((error) => {
      console.log(error);
      });
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: "#8eecec" }}>
      <div style={{ alignItems: 'center' }}>
      <h1 style={{ justifyContent: 'center', paddingLeft: 400,width: 800, fontSize: 80 ,fontWeight: "bold" }}>Time Capsule</h1>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
            <img src={logo} alt="Logo" />
        </div>
        <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Create Account</h1>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>

          <input type="username" placeholder='Create Username' value={username} onChange={(e) => setUsername(e.target.value)} style={{ borderRadius: 5,fontSize:20, textAlign: 'center', height: 30, width: 250, marginBottom: '10px' }} />

          <input type="password" placeholder='Create Password' value={password} onChange={(e) => setPassword(e.target.value)} style={{ borderRadius: 5,fontSize:20, textAlign: 'center', height: 30, width: 250, marginBottom: '10px'}} />
          <br />
          <button onClick={handleRegister} style={{fontSize: 15, borderRadius: 5, height: 30, width: 100, fontWeight: 'bold' , marginTop: '10px' }}>Submit</button>
        </div>
        <p style={{ fontWeight: 'bold', textAlign: 'center', marginTop: '20px' }}>Already have an account?  <Link to="/login" style={{ fontWeight: 'bold', textDecoration: 'underline', color: 'blue' }}>Log in</Link></p>
      </div>
    </div>
  );
}

export default RegisterPage;
