import React, { useState } from 'react';
import logo from '../../src/logoTC.png';

function MessagePage() {
  const [message, setMessage] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  const handleChoose = () => {
    // handle choose date and time logic here
    console.log(`Choosing date: ${date} and time: ${time}`);
  };

  const handleSubmit = () => {
    // handle submit logic here
    console.log(`Submitting message: ${message}`);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ width: '400px', justifyContent: 'center', alignItems: 'center' }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
          <img src={logo} alt="Logo" />
        </div>
        <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Leave Your Message!</h1>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <label style={{ marginBottom: '10px', fontWeight: 'bold' }}>
            Message:
          </label>
          <textarea rows={8} value={message} onChange={(e) => setMessage(e.target.value)} style={{ width: '75%', marginBottom: '10px' }} />
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', width: '75%' }}>
            <button onClick={handleChoose} style={{ fontWeight: 'bold', marginTop: '10px' }}>Choose Date and Time</button>
            <div>
              <label style={{ marginBottom: '10px' }}>
                Date:
              </label>
              <input type="date" value={date} onChange={(e) => setDate(e.target.value)} style={{ marginBottom: '10px' }} />
              <label style={{ marginBottom: '10px' }}>
                Time:
              </label>
              <input type="time" value={time} onChange={(e) => setTime(e.target.value)} style={{ marginBottom: '10px' }} />
            </div>
            <button onClick={handleSubmit} style={{ fontWeight: 'bold', marginTop: '10px' }}>Submit</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MessagePage;
