import React, { useState } from 'react';
import logo from '../src/logoTC.png';

function MessagePage() {
  const [message, setMessage] = useState('');
  const date = new Date();
  const options = { weekday: 'long', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true };
  const dateString = date.toLocaleDateString('en-US', options);

  const handleSendMessage = () => {
    // handle sending message logic here
    console.log(`Sending message: ${message} for date/time: ${dateString}`);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '50px' }}>
        <img src={logo} alt="Logo" />
      </div>
      <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Leave your message!</h1>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <label style={{ fontWeight: 'bold', marginBottom: '10px' }}>
          Message:
        </label>
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} style={{ width: '75%', height: '200px', marginBottom: '10px' }} />
        <div style={{ display: 'flex', justifyContent: 'space-between', width: '75%' }}>
          <button style={{ fontWeight: 'bold', marginTop: '10px' }}>Choose Time ({dateString})</button>
          <button onClick={handleSendMessage} style={{ fontWeight: 'bold', marginTop: '10px' }}>Submit</button>
        </div>
      </div>
    </div>
  );
}

export default MessagePage;
