import React, { useState } from 'react';
import logo from '../src/logoTC.png';

function MessagePage() {
  const [message, setMessage] = useState('');
  const [chosenTime, setChosenTime] = useState('');

  const handleTimeChange = () => {
    const date = new Date();
    const options = { weekday: 'long', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric', hour12: true };
    const dateString = date.toLocaleDateString('en-US', options);
    setChosenTime(dateString);
  };

  const handleSubmit = () => {
    console.log(`Submitting message: ${message} at ${chosenTime}`);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <img src={logo} alt="Logo" style={{ marginBottom: '20px' }} />
      <h1 style={{ textAlign: 'center', fontFamily: 'Lobster Two', fontStyle: 'italic' }}>Leave Your Message!</h1>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '90%' }}>
        <label style={{ marginBottom: '10px', fontWeight: 'bold' }}>Message:</label>
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} style={{ height: '150px', marginBottom: '10px', padding: '10px', fontSize: '16px', borderRadius: '5px', borderColor: 'gray' }} />
        <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%' }}>
          <button onClick={handleTimeChange} style={{ fontWeight: 'bold' }}>Choose Time: {chosenTime}</button>
          <button onClick={handleSubmit} style={{ fontWeight: 'bold' }}>Submit</button>
        </div>
      </div>
    </div>
  );
}

export default MessagePage;
